import React, { Component } from 'react'
import Toolbar from './Toolbar/Toolbar'
import { connect } from 'react-redux';
import {withRouter} from 'react-router-dom';
import Chat from '../Chat/Chat'
import AOS from 'aos'

class Layout extends Component {
    state= {
        open: false,
    }

    componentDidMount =()=>{
        AOS.init()
        axios.get(`https://movies-27cd5.firebaseio.com/${localStorage.getItem('id')}/user.json/`)
        .then(response => {this.setState(response.data.name)})
        .catch(err => {}) 
    }

    displaySidebar = () => (
        this.setState((prevstate) =>{
          return {open: !prevstate.open}
        })
    )

    render() {
        let show = (localStorage.hasOwnProperty('toolbar') || localStorage.hasOwnProperty('token')===false)

        return (
            <React.Fragment>
               { show && <Toolbar open={this.displaySidebar}/> } 
                <main>
                    {this.props.children}
                </main>
               { show && <footer style={{position:'fixed',bottom:'10px'}}>   
                    <Chat/>
                    </footer>} 
            </React.Fragment>
        )
    }
}


const mapStateToProps = state => {
    return {
      toolbar: state.auth.toolbar
    };
  };


  export default withRouter(connect(mapStateToProps) (Layout));
