import React, { useEffect, useState } from 'react';
import './Achat.css'
import {Card} from 'react-bootstrap'
import photo from '../../../assets/images/netflix-logo.jpg';
import axios from 'axios';

const Achat = () => {
    const [orderUser, setOrderUser] = useState([]);
    useEffect(() => {
      axios.get(`https://movies-27cd5.firebaseio.com/${localStorage.getItem('id')}/Order.json/`)
        .then(res => {
          res.data !== null && setOrderUser(res.data)
        })
        .catch(err => {})   
    }, [])

    let order;
    if (orderUser.length === 0)
        order = <p style={{fontStyle: 'italic', color: 'black'}}>Aucune commande.</p>

        else {
            order = orderUser.map((order, index) => (
              <div key={index} style={{marginTop:'40px', marginBottom:'30px'}}>
                   {
          props.similar.map(movie => {
            return (
              <div key={movie.id} className='blockImage' onClick={() => props.history.push(`/movie/${movie.id}`)}>
                <Image style={{height:'400px'}} src={`https://image.tmdb.org/t/p/w500/${movie.img}`} alt={movie.title} />
                { props.component === 'MyOrder' && <h4 style={{color:'black',textAlign:'center', marginTop:'10px'}}>{movie.title}</h4> }
              </div>
            )
          })
        }
                {/* <Slider similar={order.films} title={`Commande ${order.numberOrder} du ${order.date}`} image='ImageOrder' titleCss='TitleSliderOrder' component='MyOrder'/>   */}
              </div>)
            )
          }  

    return (
    <div className='Achat'>
            <div className='blockAchat'>
                <div className='blockTitre'>
                     <h1 className='titreInBlock'> Achats </h1>
                </div>
                <div className='blockCarteAchat'>
                    <div className='blockCarte'>
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
