                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                           <Card.Img  className='cardAchat' src={photo} alt='card movie' />
                    </div>
                </div>
            </div>
    </div>
    )
}

export default Achat;
