import React from 'react'
import { useState,useEffect } from 'react'
import './CarteBleu.css'
import Cards from 'react-credit-cards';
import 'react-credit-cards/es/styles-compiled.css'
import Input from 'muicss/lib/react/input';

const CarteBleu = () => {
    const [cvc, setCvc] = useState('')
    const [focus, setFocus] = useState('')
    const [name, setName] = useState('')
    const [expiry, setExpiry] = useState('')
    const [number, setNumber] = useState('')



    const handleInputFocus = () =>{

    }

    return (
        <div className='blockImage1'>
            <div className='TitreContainerCarte'>
                <h2 className='titreInContainerCarte'> Carte Bleue </h2>   
                    <div className='blockCartebleu'>
                        <Cards className='Cartebleu'
                            cvc={cvc}
                            expiry={expiry}
                            focused={focus}
                            name={name}
                            number={number}
                            />
                            <form className='InputCartebleu'>
                                <Input  label="Card Number" minLength="16" maxLength="16"  value={number} onChange={(e) => setNumber(e.target.value)} onFocus={handleInputFocus}  type="name"  pattern= "[0-9]" floatingLabel={true} />
                                <Input  label="Name" minLength="2" maxLength="25"  value={name} onChange={(e) => setName(e.target.value)} onFocus={handleInputFocus}  type="name" floatingLabel={true} />
                            <div className='cvcDateCartebleu'>
                                <Input  label="Date expiry" minLength="4" maxLength="4" type="date" value={expiry} onChange={(e) => setExpiry(e.target.value)} onFocus={handleInputFocus}  type="name" floatingLabel={true} />
                                <Input  label="CVC" minLength="3" maxLength="3"  value={cvc} onChange={(e) => setCvc(e.target.value)} onFocus={handleInputFocus}  type="name" floatingLabel={true} />
                            </div>
                            </form>
                    </div>
            </div>
        </div>
    )
}

export default CarteBleu;
