import React, { useEffect, useState } from 'react';
// import { useSelector, useDispatch } from 'react-redux';
// import * as actions from '../../store/actions/index';
// import {Modal} from 'react-bootstrap'
import './Chat.css';
import N from '../../assets/images/netflix-logo.png';
// import { Widget } from 'react-chat-widget';
import io from 'socket.io-client'
import { Widget } from 'react-chat-widget';
import 'react-chat-widget/lib/styles.css';
//const socket = io("http://localhost:3000")



const Chat = () => {


     const [msgs,setMsg] = useState([])
    // const [room,setRoom] = useState('')



    useEffect (()=>{
        //instauration de la connexion
        socket.on('msg serveur:',name=> {
            console.log('msggg recu du serveur--->',name) 
            setMsg(...msgs,name)
        })

    },[])

    // const userMessage = () => {
    //     setUser()

    //   };


    const handleNewUserMessage = (newMessage) => {
        socket.emit('msg envoyer :',localStorage.getItem('name') ,newMessage)
        console.log(`New message incoming! ${newMessage}`);
        setMsg(newMessage)
        // Now send the message throught the backend API

      };

    console.log(msgs)
    let chat;
    if (localStorage.hasOwnProperty('toolbar')){
        chat = ( <div >
        <Widget title="Netflix Chat"  subtitle={'Profiter'} handleNewUserMessage={handleNewUserMessage} value={msgs} addResponseMessage={msgs} senderPlaceHolder="ecrire un message" />

         </div>)
    }

    return(
    <>
       {chat}
    </>
    )

}



export default Chat;
