import React, { useState, useEffect } from 'react'
import './MonCompte.css'
import {Link} from 'react-router-dom'
import {FiShoppingCart} from 'react-icons/fi'
import {FaRegAddressCard} from 'react-icons/fa'
import {GrGroup} from 'react-icons/gr'
import {AiOutlineHome} from 'react-icons/ai'
import { useLocation } from 'react-router-dom';
import useStateWithCallback from 'use-state-with-callback';

const MonCompte = (props) =>{

    const [color1, setColor1] = useState('NavLinkProfil');
    const [color2, setColor2] = useState('NavLinkProfil');
    const [color4, setColor4] = useState('NavLinkProfil');

    const [color5, setColor5] = useState('LogoProfil');
    const [color6, setColor6] = useState('LogoProfil');
    const [color8, setColor8] = useState('LogoProfil');

    const [color9, setColor9] = useState('LiProfil');
    const [color10, setColor10] = useState('LiProfil');
    const [color12, setColor12] = useState('LiProfil');

    let location = useLocation();
    let [pathname, setPathname] = useStateWithCallback("", ()=> color());

    const color = () =>{
        setColor1('NavLinkProfil');
        setColor2('NavLinkProfil');
        setColor4('NavLinkProfil');

        setColor5('LogoProfil');
        setColor6('LogoProfil');
        setColor8('LogoProfil');

        setColor9('LiProfil');
        setColor10('LiProfil');
        setColor12('LiProfil');

        if (pathname === '/home') {
            setColor1('NavLinkProfilClick');
            setColor5('LogoProfilClick');
            setColor9('LiClick');
        }
        else if (pathname === '/compte/InfoPerso') {
            setColor2('NavLinkProfilClick');
            setColor6('LogoProfilClick');
            setColor10('LiClick');
        }
        else if (pathname === '/compte/achats') {
            setColor4('NavLinkProfilClick');
            setColor8('LogoProfilClick');
            setColor12('LiClick');
        } 
    }

    useEffect(() => {
        setPathname(location.pathname);
    }, [setPathname, location.pathname]);


    return (
        <div className='MonCompte'>
            <div className='BlockGauche'>
                        <p className='titleMonCompte'> Mon compte </p>
                  <div className='blockButton'>
                     <div className='Button'>
                        <NavLink style={{color:'inherit',textDecoration:'none'}} to='/home'>
                            <AiOutlineHome style={{height:'100px',width:'40px'}}/>     
                        <p className='titre'> Netflix </p>
                        </NavLink>
                    </div>
                    <div className='Button'>
                        <NavLink style={{color:'inherit',textDecoration:'none'}} to='/compte/InfoPerso'>
                            <FaRegAddressCard style={{height:'100px',width:'40px'}}/>
                        <p className='titre'> Infos persos </p>
                        </NavLink>
                    </div>
                    <div className='Button'>
                    <NavLink style={{color:'black',textDecoration:'none'}} to='/compte/achats' >

                        <FiShoppingCart style={{height:'100px',width:'40px'}} />
                        <p className='titre'> Mes achats </p>
                    </NavLink>
                    </div>
                    <div className='Button'>
                    <NavLink style={{color:'black',textDecoration:'none'}} to='/compte/social' >
                        <GrGroup style={{height:'100px',width:'40px'}} />
                        <p className='titre'>  Social </p>
                    </NavLink> 
                    </div>
                  </div> 
            </div>
            <div className='BlockDroite'> 
                 {props.child}
            </div>
        </div>
    )
} 

export default MonCompte;
