import React from "react";
import Slider from 'react-slick';
import { withRouter } from 'react-router-dom';
import { Image } from 'react-bootstrap';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './Slider.css';

const SimpleSlider = (props) => {
  var settings;
  // if (props.component === 'Movie') {
  //   settings = {
  //     width: '90%',
  //     dots: true,
  //     infinite: true,
  //     slidesToShow: 4,
  //     slidesToScroll: 4,
  //     autoplay: true,
  //     autoplaySpeed: 4000,
  //     speed: 1000,
  //     initialSlide: 0,
  //     responsive: [
  //       {
  //         breakpoint: 1024,
  //         settings: {
  //           slidesToShow: 3,
  //           slidesToScroll: 3,
  //         }
  //       },
  //       {
  //         breakpoint: 780,
  //         settings: {
  //           slidesToShow: 2,
  //           slidesToScroll: 2,
  //           initialSlide: 2,
  //         }
  //       },
  //       {
  //         breakpoint: 480,
  //         settings: {
  //           slidesToShow: 1,
  //           slidesToScroll: 1,
  //         }
  //       }
  //     ]
  //   };
  // }

if (props.component === 'MyOrder')  {
    settings = {
      dots: true,
      infinite: true,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      autoplaySpeed: 4000,
      speed: 1000,
      initialSlide: 0,
    };
  }

  return (

      <Slider {...settings}>
        {
          props.similar.map(movie => {
            return (
              <div key={movie.id} className='blockImage' onClick={() => props.history.push(`/movie/${movie.id}`)}>
                <Image style={{height:'400px'}} src={`https://image.tmdb.org/t/p/w500/${movie.poster_path || movie.poster}`} alt={movie.title} />
                { props.component === 'MyOrder' && <h4 style={{color:'black',textAlign:'center', marginTop:'10px'}}>{movie.title}</h4> }
              </div>
            )
          })
        }
      </Slider>

  );
}

export default withRouter(SimpleSlider);
