import * as actionTypes from '../actions/actionTypes';
import { updateObject } from '../utility';

const initialState = {
    token: null,
    userId: null,
    error: null,
    loading: false,
    modal: false,
    photo: null,
    toolbar:false,
    chatS: false,
    chatF: false
  };
  const authStart = ( state, action ) => {
    return updateObject( state, { error: null, loading: true } );
  };
  const authSuccess = (state, action) => {
    return updateObject( state, { 
        token: action.idToken,
        userId: action.userId,
        error: null,
        loading: false,
        modal: true
     } );
  };
  const authFail = (state, action) => {
    return updateObject( state, {
        error: action.error,
        loading: false
    });
  }

  const authLogout = (state) => {
    return updateObject(state, {
        token: null,
        userId: null
    });
};


const showchat = (state,action) => {
  //0console.log('ACTiommmmm********',action)
  let chatS;
  let chatF;
  if (action.showS)
     chatS= true
  else if (action.showF)
    chatF= true
  return updateObject(state, {
      chatS: chatS,
      chatF: chatF
  });
};

const toolbar = (state,action) => {
  return updateObject(state, {
      toolbar: action.toolbar
  });
};

const photo = (state, action) => {
  return updateObject(state, {
    photo : action.photo
});
}


  const reducer = ( state = initialState, action ) => {
    switch ( action.type ) {
        case actionTypes.AUTH_START: return authStart(state, action);
        case actionTypes.AUTH_SUCCESS: return authSuccess(state, action);
        case actionTypes.AUTH_FAIL: return authFail(state, action);
        case actionTypes.AUTH_LOGOUT: return authLogout(state);
        //case actionTypes.MODAL_FALSE: return modal_false(state);
        case actionTypes.PHOTO: return photo(state, action);
        case actionTypes.TOOLBAR: return toolbar(state, action);
        case actionTypes.SHOWCHATS: return showchat(state, action);
        default:
            return state;
    }
  };
  export default reducer;
